import React from "react";
import Book from "../components/Book";

class Bookshelf extends React.Component{

  state = {
    searchBook: ''
  }

  render(){
  const bookComponents = props.booksForShelf.map(book => <Book book={book} key={`shelf-${book.id}`} clickHandler={props.removeBookFromShelf}/>)
  return (
    <div>
      <h1>Book Shelf</h1>
      <input name='search' value={this.state.searchBook} type='text' onChange={this.changeHandler}></input>
      <button onClick={this.clickHandler}>Search</button>
      <ul>{bookComponents}</ul>
    </div>
  );
  }

};

export default Bookshelf;
