import React from "react";
import Book from "../components/Book";

class Bookshelf extends React.Component{

  state = {
    search: ''
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render(){
  const bookComponents = this.props.booksForShelf.map(book => <Book book={book} key={`shelf-${book.id}`} clickHandler={this.props.removeBookFromShelf}/>)
  return (
    <div>
      <h1>Book Shelf</h1>
      <input name='search' value={this.state.search} type='text' onChange={this.changeHandler}></input>
      <button onClick={this.clickHandler}>Search</button>
      <ul>{bookComponents}</ul>
    </div>
  );
  }

};

export default Bookshelf;
