import React from "react";

class Form extends React.Component{
  render() {
    return (
      <form onSubmit={this.submitHandler}>
        <input name='name' value={this.state.name} onChange={this.changeHandler}>Title:</input>
        <input name='author' value={this.state.author} onChange={this.changeHandler}>Author:</input>
        <input name='img' value={this.state.img} onChange={this.changeHandler}>Image URL:</input>
        <button>Submit</button>
      </form>
    )
  }


}

export default Form;
