import React, { Component } from "react";
import Form from "../components/Form";
import Book from "../components/Book";

class BookList extends React.Component{

  state = {
    bookList: []
  }

  componentDidMount(){
    fetch('http://localhost:3005/books')
    .then(resp => resp.json())
    .then( data => {
      this.setState({
        bookList: data
      })
    })
  }

  submitCommentHandler = (bookObj, comment) => {
    console.log(book.comment)
    const updatedList = this.state.bookList.map(book => {
      if (book.id === bookObj.id){
        book.comments.push(comment)
      }
        return book
    })
    this.setState ({
      bookList: updatedList
    })
  }

  render(){

    const bookComponents = this.state.bookList.map(book => <Book book={book} key={book.id} submitCommentHandler = {this.submitCommentHandler}/>)

    return (
      <div className="book-list">
        <h1>Book List</h1>
        <Form />
        <ul>{bookComponents}</ul>
      </div>
    );
  }

}

export default BookList;
