import React from "react";

class Book extends React.Component{

  state ={
    showComment: false
  }

  render(){
    return (
      <div>
        <br></br>
        <h2>{props.book.title}</h2>
        <img src= {props.book.img}></img>
        <br></br>
        <button onClick={() => <div>HI</div>}>Add Comment</button>
        <button>See Comments</button>
      </div>
    );
  }

};

export default Book;
