import React from "react";

class Book extends React.Component{

  state ={
    showComment: false,
    showCommentForm: false,
    comment: ''
  }

  showCommentsHandler = () =>{
    this.setState({
      showComment: !this.state.showComment
    })
  }

  showCommentsFormHandler = () =>{
    this.setState({
      showCommentForm: !this.state.showCommentForm
    })
  }

  submitCommentHandler = (e) => {
    this.setState({
      showCommentForm: !this.state.showCommentForm
    })
    this.props.submitCommentHandler(this.props.book, e.target.comment.value)
  }

  changeHandler = (e) => {
    // this.setState({
    //   [e.target.name]: e.target.value
    // })
  }


  render(){
    const commentsForm = <form>
        <input type='text' name='comment' value={this.state.comment} onChange={this.changeHandler}></input>
        <button>submit</button>
      </form>

    return (
      <div>
        <br></br>
        <h2>{this.props.book.title}</h2>
        <img src= {this.props.book.img} alt={this.props.book.title}></img>
        <br></br>
        <button onClick={this.showCommentsFormHandler}>Add Comment</button>
        {this.state.showCommentForm ? commentsForm : null}
        <button onClick={this.showCommentsHandler}>See Comments</button>
        {this.state.showComment ? <ul>{this.props.book.comments}</ul> : null}
      </div>
    );
  }

};

export default Book;
