  state = {
    booksForShelf: []
  }

  addBookToShelf = (book) => {
    console.log('clicked')
    if (!this.state.booksForShelf.includes(book)){
      this.setState({
        booksForShelf: [...this.state.booksForShelf, book]
      })
    }
  }

  removeBookFromShelf = (bookObj) => {
