      book.id !== bookObj.id
    })
  }

  render () {
    return (
      <div className="book-container">
        <BookList addBookToShelf={this.addBookToShelf}/>
        <Bookshelf booksForShelf={this.state.booksForShelf} removeBookFromShelf={this.removeBookFromShelf}/>
      </div>
    );
  }

}

export default App;
